import { Component } from '@angular/core';
import { HttpService } from 'src/app/services/http/http.service';
import { DatePipe } from '@angular/common';
@Component({
  selector: 'app-cause-list',
  templateUrl: './cause-list.component.html',
  styleUrls: ['./cause-list.component.css']
})
export class CauseListComponent {
  cases: any;
  sno: string = '';
  cno: string = '';
  name: string = '';
  date: string = '';
  constructor(private http: HttpService, private datePipe: DatePipe){}
  ngOnInit(){
    this.http.get_cases().subscribe({
      next: data => {
        this.cases = data
      },
      error: err => {
        console.log(err);
      }
    })
  }
  onCreateCauseList(){
